import streamlit as st
st.set_page_config(page_title="MyStreamlit",page_icon="shark")
mymenu=st.sidebar.selectbox("My Menu",("Home",'Fill Form','Downloads'))
st.image("data:image/png;base64,")
st.title("Onlie Technilogy")
st.header("by Vaishali Nigam")
st.text("This is a tutorial or Streamlit Library")
if(mymenu=="Home"):
    st.markdown("<center><h1>Welcome</h1></center>",unsafe_allow_html=True)
    st.video("https://www.youtube.com/watch?v=_uQrJ0TkZlc")
elif(mymenu=="Fill Form"):
     with st.form('My Form'):
        name=st.text_input("Enter your Name")
        dob=st.date_input("Enter your Date of Birth")
        marks=st.slider("Choose Marks")
        k=st.form_submit_button("Submit")
     if k:
          st.write('Name:',name,'dob:',dob,'marks:',marks)

elif(mymenu=="Dawnloads"):
         st.header("Dawnloads")
         st.dawnload_button('Dawnload Now'," Hello this is the dawnload data",Onlie.txt)

